INSERT INTO `news` VALUES ('12', '3', 'Đây là tiêu đề của bài viết', 'Loan vừa nhắm mắt để nhận hoa theo lời đề nghị của chồng, Kha Ly bất ngờ dùng dây xích trói tay chị này vào ổ khóa trong tình trạng khỏa thân. Vị bác sĩ còn dọa sẽ cắt cổ, chặt ngón tay, rạch mặt chị Loan…', 'http://genk2.vcmedia.vn/thumb_w/600/dTUDlvZ9Pm5DNNPFeHeiGmyg5Bq4ag/Image/2014/05/lj0506cos27-f471b.jpg', '2014-05-09', '0');
